import {
    ADD_FAV,
    REMOVE_FAV,
    INITIAL_FAVS,
    REMOVE_ALL_FAVS,
  } from "../../actions/favActions";
  import { toast } from "react-toastify";
  
  const initialState = {
    favs: [],
}
const key ="fav";


function writeToLocalStorage(data) {
    localStorage.setItem(key, JSON.stringify(data));
  }

function readFromLocalStorage() {
    return JSON.parse(localStorage.getItem(key));
  }

  function getInitialFavs(key) {
    const savedFavs = localStorage.getItem(key);
    if (savedFavs) {
      return readFromLocalStorage(key);
    } else {
      return initialState.favs;
    }
  }
const reducer = (state = initialState, action) => {
    switch (action.type) {

        case ADD_FAVORITE:
            return {
                ...state,
                favorites: state.favorites.filter(item => item.id == action.payload.id).length === 0 ? [...state.favorites, action.payload] : state.favorites
            }

        case REMOVE_FAVORITE:
            return {
                ...state,
                favorites: state.favorites.filter(item => (action.payload.id !== item.id))
            }

        default:
            return state;
    }
}

export default reducer;
