import { ADD_MOVIE, BACK_TO_FIRST, NEXT_MOVIE, PREVIOUS_MOVIE, DELETE_MOVIE } from "../actions/movieActions"
import { movies, siraNo } from "../../movies"


const initialState = {
    movies: movies.slice(0,5),
    place: 0,
}

function writeToLocalStorage(data) {
    localStorage.setItem(key, JSON.stringify(data));
  }
  
  function readFromLocalStorage() {
    return JSON.parse(localStorage.getItem(key));
  }
  
  function getInitialMovies(key) {
    const savedMovies = localStorage.getItem(key);
    if (savedMovies) {
      return readFromLocalStorage(key);
    } else {
      return initialState.movies;
    }
  }

const reducer = (state = initialState, action) => {

    switch (action.type) {

        case ADD_MOVIE:
            return {
                ...state,
                movies: [...state.movies, action.payload],
            }

        case BACK_TO_FIRST:
            return {
                ...state,
                siraNo: 0
            }

        case NEXT_MOVIE:
            return {
                ...state,
               
                siraNo: state.siraNo === (state.movies.length - 1) ? state.siraNo : (state.siraNo + action.payload)
            }

        case PREVIOUS_MOVIE:
            return {
                ...state,
                siraNo: state.siraNo === 0 ? state.siraNo : (state.siraNo - action.payload)
            }

        case DELETE_MOVIE:

            return {
                ...state,
                movies: state.movies.filter(item => ((action.payload.id) !== (item.id))), 
                siraNo: (state.movies.length - 1) === state.siraNo ? 0 : state.siraNo
            }

        default:
            return state;
    }
}

export default reducer;
